$(document).ready(function () {
    $('.start-order').click(function (e) {
        const id = $(this).attr('id')
        const price = $(this).attr('price')
        e.preventDefault()
        $('.hidden').arcticmodal()
        $('.id-hidden').val(id)
        $('.price-hidden').val(price)
    })
    $('.hidden').submit(function (e) {
        let form = $(this)
        e.preventDefault();
        $.ajax({
            type: "POST",
            url: "/setOrder",
            data: form.serialize(),
            success: function (result) {
                let data = JSON.parse(result)
                if(data.status === 'success') {
                    $('.box-modal_close').trigger('click')
                } else {
                    alert('SOME WRONG')
                    $('.box-modal_close').trigger('click')
                }
            }
        });
    })
})