$(document).ready(function () {
    function scrolId(id) {

        $('html body').animate({
            scrollTop: $(id).offset().top
        }, 1000)
        return false
    }
    $('.start-order').click(function (e) {
        const id = $(this).attr('id')
        const price = $(this).attr('price')
        const date = new Date().toLocaleString();

        e.preventDefault()
        $('.hidden').arcticmodal()
        $('.id-hidden').val(id)
        $('.price-hidden').val(price)
        $('.dataOrder').val(date)
    })
    $('.hidden').submit(function (e) {
        let form = $(this)
        e.preventDefault();
        $.ajax({
            type: "POST",
            url: "/setOrder",
            data: form.serialize(),
            success: function (result) {
                let data = JSON.parse(result)
                if (data.status === 'success') {
                    $('.box-modal_close').trigger('click')
                } else {
                    alert('SOME WRONG')
                    $('.box-modal_close').trigger('click')
                }
            }
        });
    })
    $('.menu-link').click(function (e) {
        // e.preventDefault()
        $('html, body').animate({
            scrollTop: $($(this).attr('href')).offset().top
        }, 500, 'linear');
    })
})