$(document).ready(function () {
    $(window).scroll(function () {
        let top = $(document).scrollTop()
        if (top < 300) {
            $('.main-header').removeClass('sticky')
            $('.link-cont-wrapper').removeClass('stick')
        }
        else {
            $('.main-header').addClass('sticky')
            $('.sticky').animate({
                opacity: '1'
            }, 500)
            $('.link-cont-wrapper').addClass('stick')
            //sticky
        }
    })
    $('.menu-link').click(function (e) {
        // e.preventDefault()
        $('html, body').animate({
            scrollTop: $($(this).attr('href')).offset().top
        }, 500, 'linear');
    })
    $('.parallax-window').parallax()
    $('.bay-service').click(function (e) {
        e.preventDefault()
        $('.hidden-form').arcticmodal()
        let d = new Date().toLocaleString();;


        let name = $(this).attr('id')
        let price = $(this).attr('price')
        $('#priceInp').val(price)
        $('#dataOrder').val(d)
        $('#nameInp').val(name)
    })
    $('.input-label input').focus(function (e) {
        e.preventDefault()
        $('.input-label p').animate({
            top: '4px',
            fontSize: '12px'
        }, 350)
    })
    $('.hidden').submit(function (e) {
        e.preventDefault()
        let form = $(this)
        $.ajax({
            type: "POST",
            url: "/setOrder",
            data: form.serialize(),
            success: function (response) {

                $('.box-modal_close ').trigger('click')
            }
        });
    })
    $('.user-agent_mode').click(function (e) {
        e.preventDefault()
        let c = $('<div class="hidde-assectt box-modal" />');
        c.html($('.b-text').html());
        $.arcticmodal({
            content: c
        });
    })
    $('.hamburger').click(function(e) {
        e.preventDefault()
        $(this).toggleClass('is-active')
        $('.mobile-nav').animate({
            opacity: 1,
            bottom: '0px'
        }, 400)
    })
    $('.close-nav-mobile').click(function(e) {
        e.preventDefault()
        
        $('.mobile-nav').animate({
            opacity: 0,
            bottom: '200px'
        }, 400)
        setTimeout(function() {
            $('.hamburger').removeClass('is-active')
        }, 450)
    })
})